workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*
import com.rameses.rulemgmt.action.*;

class RuleActionFieldListHandler extends RuleActionHandler {

    @Service("RuleMgmtService")
    def service;

    def varList;

    void init() {
        if(varList) return;
        String lookupkey = actionParamDef.lookupkey;
        if(!lookupkey)
            throw new Exception("Var lookup key not exit " + lookupkey );
        def fact = action.params.find{ it.actiondefparam.handler == "var" && it.actiondefparam.name == lookupkey  }?.var;
        if( fact == null )
            throw new Exception("Fact reference does not exist");
        //lookup fields in the fact. 
        varList =  service.getFactFields( [objid: fact.datatype] );
        if(!varList)
            throw new Exception("No fields found for " + lookupkey );
    }
}
