workunit.code:
class DatePrompt {

    def date = new java.sql.Date( System.currentTimeMillis()); 
    def handler;
    String title;

    def doCancel() {
        return "_close";
    }
    def doOk() {
        if(!handler) throw new Exception("Please specify handler in date:prompt");
        if(handler) handler(date);
        return "_close";
    }
}
