import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class SelectColumnController {

    def schema;
    def columnList;  
    def onselect;            

    void init() {
        //!it.visible == false means if it.visible false is sepcified. by default it.visible
        //is null or true.  
        columnList = schema.columns.findAll{ !it.primary && !it.jointype && (it.visible==null || it.visible=='true' ) };
    }                

    def doCancel() {
        return "_close";
    }

    def doOk() {
        if( onselect)  {
            onselect( columnList.findAll{it.selected == true } );
        }
        return "_close";
    }
}
